import Taro from "@tarojs/taro";
import { View } from "@tarojs/components";
import { AtGrid } from "taro-ui";
import HeaderNavBar from "@/common/navbar/header";
import styles from "./index.module.scss";

function IndexPage() {
  const onGridClick = (item, index) => {
    const urls = ["/pages/2k20/lockercode", "/pages/epic/free", "/pages/steam/free"];
    Taro.navigateTo({
      url: urls[index]
    });
  };

  return (
    <View className={styles.indexPage}>
      <HeaderNavBar title='忆游GMemory' />
      <View
        style={{
          marginTop: Taro.getEnv() === "WEAPP" ? "0px" : "46px"
        }}
      ></View>
      <AtGrid
        hasBorder={false}
        onClick={onGridClick}
        data={[
          {
            image: "http://linyu.dynv6.net:9001/images/icons/icon-2k20.jpg",
            value: "2K20储物柜"
          },
          {
            image:
              "data:image/png;base64,",
            value: "EPIC免费"
          },
          {
            image:
              "http://linyu.dynv6.net:9001/images/icons/icon-steam.png",
            value: "Steam免费"
          }
        ]}
      />
    </View>
  );
}

IndexPage.config = {
  navigationBarTitleText: "忆游GMemory"
};

export default IndexPage;
